mapper.insert:
        INSERT INTO CUSTOMERS (CUSTOMER_ID, 
        					CUSTOMER_NAME, 
					        PASSWORD,
					        SSN,
					        PHONE,
					        CITY,
					        ADDRESS,
					        POSTAL_CODE) 
        VALUES ( #{customerID}
	           	 ,#{customerName}
	            	,#{password}
	           		,#{ssn}
	            	,#{phone}
	            	,#{city}
	            	,#{address}
	            	,#{postalCode})

mapper.update:
  UPDATE CUSTOMERS
		   SET PHONE = #{phone}
		  ,CITY = #{city}
		  ,ADDRESS= #{address}
		  ,POSTAL_CODE = #{postalCode} 
		  ,PASSWORD = #{password} 
  WHERE CUSTOMER_ID LIKE #{customerID}
